    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        Deportist objeto = new Deportist();
        objeto.setnombre(txtNombre.getText());
        objeto.setPeso(Double.parseDouble(txtPeso.getText()));
        objeto.setAltura(Double.parseDouble(txtAltura.getText()));
        
        txalinfo.append(objeto.mostrarInfo());

        // TODO add your handling code here:
    }//GEN-LAST:event_btnCalcularActionPerformed

